	//如果当前系统没有学生的话 
	if (count == 0) {
	    printf("当前系统没有学生哦。\n");
	    return;
	}
   	//调用quickSort函数对学生成绩进行降序排序
    quickSort(students, 0, count - 1);

    //打印排序后的学生信息
    printf("这是同学们的考试成绩排名哦：\n");
    //遍历打印学生按成绩排名的信息 
    for (int i = 0; i < count; i++) {
        printf(" 第%d名:  姓名：%s  学号：%lld   成绩：%.2f\n", i + 1, students[i].name, students[i].id, students[i].score);
    }
}

