//指定位置插入
int insertElem(SeqList*L,int pos,ElemType e){
	if(L->length>=MAXSIZE){
		printf("表已经满了\n");
		return 0;
	}
	if(pos<1||pos>L->length){
		printf("插入位置错误\n");
