#include <stdio.h>
int main(){
	int a[]={15,22,67,43,87};
	int *p;
	p=a;
//	printf("%p\n",a);
//	printf("%p\n",p);
//	printf("%d\n",*p);
	int len=sizeof(a)/sizeof(a[0]);
	for(int i=0;i<len;i++){
